import 'package:flutter/material.dart';class Dashboard extends StatelessWidget {  String welcome;  Dashboard(this.welcome);  @override  Widget build(BuildContext context) {    return Scaffold(      body:  Column(        mainAxisAlignment: MainAxisAlignment.center,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          SizedBox(width: MediaQuery.of(context).size.width,),         Text(welcome)      ],),    );  }}