import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';import 'package:google_sign_in/google_sign_in.dart';import 'Dashboard.dart';class Google extends StatefulWidget {  const Google({Key? key}) : super(key: key);  @override  _GoogleState createState() => _GoogleState();}class _GoogleState extends State<Google> {  GoogleSignIn googleSignIn = GoogleSignIn();  GoogleSignInAccount? _user;  GoogleSignInAccount get user=> _user!;  @override  Widget build(BuildContext context) {    return Scaffold(      body: Column(        mainAxisAlignment: MainAxisAlignment.center,        crossAxisAlignment: CrossAxisAlignment.center,        children: [        SizedBox(width: MediaQuery.of(context).size.width,),        FlatButton(onPressed: (){          googleLogin();        }, child:        Text("Google"),        color: Colors.redAccent,        ),      ],),    );  }    Future googleLogin() async{    final googleUser=await googleSignIn.signIn();    if(googleUser==null) return;    _user = googleUser;    final googleAuth=await  googleUser.authentication;    final credential=GoogleAuthProvider.credential(        accessToken: googleAuth.accessToken,      idToken: googleAuth.idToken,    );    final result=await FirebaseAuth.instance.signInWithCredential(credential);    if(result.user!=null){      Navigator.push(context, MaterialPageRoute(builder: (context)=> Dashboard(googleUser.email)));    }  }}